package com.sample1;

public class operands {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int a = 10;
           int b = 5;
           System.out.println(a+b);
           System.out.println(a-b);
           System.out.println(a/b);
           System.out.println(a*b);
           System.out.println(a%b);
           System.out.println(a>b);
           System.out.println(a<b);
           System.out.println(a>5 && b>10);
           System.out.println(a>5 || b>10);
           System.out.println(a==b);
           System.out.println(a++);
           System.out.println(++a);
           System.out.println(++b);
           System.out.println(b++);
           System.out.println(a--);
           System.out.println(--a);
           System.out.println(--b);
           System.out.println(b--);
	}

}
